mapper.update:
UPDATE BOOK_BORROW SET RETURN_DATE= SYSDATE, BORROW_STATUS='N'
WHERE BOOK_SEQ IN
#{book_seq}

UPDATE BOOK_USERINFO
SET PENALTY_DATE = SYSDATE + 2+(
    SELECT SUM(TO_NUMBER(TRUNC(SYSDATE) - TRUNC(RETURN_DATE)))
    FROM BOOK_BORROW
    WHERE SYSDATE > RETURN_DATE AND USER_ID = #{user_id}
)
WHERE USER_ID = #{user_id}
