UPDATE BOOKINFO
SET GENRE_CODE=#{genre_code}
WHERE BOOK_CODE IN

	#{book_code}

UPDATE BOOK_STATUS
SET STATUS_CODE=#{status_code}
WHERE BOOK_SEQ IN

	#{book_seq}

